mapper.update:
UPDATE board_notice
	SET bo_title = #{bo_title},
		bo_content = #{bo_content}
			WHERE bo_idx = #{bo_idx}

UPDATE board_notice
	SET bo_idx = bo_idx - 1
	WHERE bo_idx > #{bo_idx}

mapper.insert:

	SELECT IFNULL(MAX(qst_idx), 0) FROM qst_board

	INSERT 
		INTO qst_board
		VALUES (
			#{qst_idx} + 1
			, #{mem_idx}
			, #{mem_name}
			, #{qst_pass}
			, #{qst_subject}
			, #{qst_content}
			, #{qst_idx} + 1 -- qst_board_re_ref
			, 0
			, 0
			, now()
			, #{qst_type}
			, 'N'
		)

mapper.select:
SELECT *
	FROM qst_board
		
			WHERE qst_type = #{searchType}
		
		ORDER BY
			qst_board_re_ref DESC
			, qst_board_re_seq ASC
	LIMIT
		#{startRow}
		, #{listLimit}
	WHERE mem_idx = #{mem_idx}

SELECT COUNT(*)
	FROM qst_board
		
			WHERE qst_type = #{searchType}
