mapper.select:
SELECT ci.cream_idx, pi.image_path, pi.image1, ci.cream_title, ci.size220, ci.cream_readcount, COUNT(d.dibs_idx) AS dibs_count FROM cream_item ci LEFT JOIN product_image pi ON ci.cream_idx = pi.product_idx LEFT JOIN dibs d ON ci.cream_idx = d.product_idx GROUP BY ci.cream_idx, pi.image_path, pi.image1, ci.cream_title, ci.size220, ci.cream_readcount
ORDER BY cream_readcount desc
limit #{startRow} , #{listLimit}

mapper.delete:
delete from
    dibs
where
    mem_idx = #{mem_idx }
    and product_idx = #{cream_idx }

mapper.insert:
	   insert into
            courier_info
        values(
            null
            , #{request_idx}
            , #{seller_idx}
            , #{buyer_idx}
            , #{tracking_company}
            , #{tracking_code}
            , #{tracking_number}
            , ''
            , now()
        )

mapper.select:
select COUNT(*) 
from courier_info
where product_idx = #{request_idx}
