mapper.select:
SELECT * 
FROM chat_rooms
WHERE 
	mem_seller_idx = #{mem_idx}
	AND chat_room_area = #{chat_room_area}
;

SELECT * 
FROM chat_rooms
WHERE 
	mem_buyer_idx = #{mem_idx}
	AND chat_room_area = #{chat_room_area}
;

SELECT * 
FROM chat_rooms
WHERE 
	(
		mem_buyer_idx = #{mem_idx}
		or mem_seller_idx = #{mem_idx}
	)
	AND chat_room_idx = #{chat_room_idx}
;
