mapper.insert:
INSERT
INTO social_follows
VALUES (
null
, #{follower_idx}
,
#{followee_idx}
, now()
)

mapper.delete:
DELETE
FROM social_follows
WHERE follower_idx =
#{follower_idx}
AND followee_idx = #{followee_idx}

mapper.select:
SELECT
COUNT(*)
FROM social_follows
WHERE follower_idx = #{follower_idx}
AND
followee_idx = #{followee_idx}

SELECT COUNT(*)
FROM
social_follows
WHERE follower_idx = #{sId}
AND followee_idx = #{mem_idx}
